<fieldset class='fs-info'><legend class='history-logs'>History Logs</legend><?if(count($history)==0){?>	<br>	<span class='warning'>No history found.</span><?}else{?>	<table width=90%>		<tr class='header'>			<td width=15%>datetime</td>			<td width=60%>remarks</td>			<td>callresult</td>			<td>user</td>		</tr>		<?foreach($history as $data){?>			<tr class='list'>				<td valign=top><?=$data['time_stamp']?></td>				<td valign=top><?=nl2br($data['remarks'])?></td> 				<td valign=top>					<?=(isset($cr[$data['callresult']]) ? $cr[$data['callresult']] : '')?>										<?if(in_array($data['callresult'],$withSubCR)){?>						<?=isset($subCr[$data['sub_callresult']]) ?  "(<i>{$subCr[$data['sub_callresult']]} </i>)" : ''?>					<?}elseif($data['callresult'] == 'AG'){?>                        (<?=isset($ag_type[$data['ag_type']]) ? $ag_type[$data['ag_type']] : ''?>)                    <?}?>				</td> 				<?php					$agent_id = ($data['user_id']);				?>				<td valign=top><?=(isset($users[$agent_id]) ? $users[$agent_id] : 'n/a')?></td>			</tr>		<?}?>	</table><?}?></fieldset>