<div class='page-header'>users list</div><input type='checkbox' id='chk-active-user' <?=($showAll=='1') ?'checked=checked':''?>><span class='note' >Show all users</span><div id='div-users'><table>	<tr>		<td>active</td>		<td>user id</td>		<td>user name</td>		<td>type</td>		<td></td>	</tr>	<?foreach($users as $d){?>	<tr>		<td>			<?if(isset($privs[182])){?>					<input type='checkbox' class='chk-active-ctrl-user' username='<?=$d['user_name']?>'  <?=($d['is_active']=='1') ?'checked=checked':''?>>			<?}?>		</td>		<td><?=$d['user_name']?></td>		<td><?=$d['firstname']. ' ' . $d['lastname']?></td>		<td><?=$userTypes[$d['user_type']]?></td>		<td>			<?if(isset($privs[181])){?>			<a class= 'a-edit-user' href='<?=base_url()?>users/edit/<?=$d['user_name']?>'>edit</a>			<?}?>		</td>	</tr>		<?}?></table></div><script>	$(document).ready(function(){	    $("#chk-active-user").click(function(){            var val = $(this).prop('checked');            var showInActive = 0;            if(val == true)                showInActive = 1;            window.location ='<?=base_url()?>users/index/'+showInActive;        });        $('.chk-active-ctrl-user').click(function(){            var username = $(this).attr('username');            var val = $(this).prop('checked');            var active  = 0;            if(val == true)                active = 1;            var data ={};            data['user_name'] =username;            data['is_active'] =active;            $.ajax({                url:'<?=base_url()?>users/active/',                data:data,                type:'POST',                success:function(data){                   // alert(data);                }            })        })		$(".a-edit-user").click(function(){			$.ajax({				url:$(this).prop('href'),				success:function(data){					$("#div-users").html(data);				}			})			return false;		})	})</script>